// Import YouTube IFrame Player API
  const tag = document.createElement('script');
  tag.src = 'https://www.youtube.com/iframe_api';
  document.head.appendChild(tag);

  // Initialize variables
  let apiKey = localStorage.getItem('apiKey') || '';
  let searchQuery = '';
  let videos = [];
  let playlist = JSON.parse(localStorage.getItem('playlist')) || [
    { title: 'Example Video 1', channel: 'Example Channel 1', id: 'VIDEO_ID_1', thumbnail: 'THUMBNAIL_URL_1', duration: 'DURATION_1' },
    { title: 'Example Video 2', channel: 'Example Channel 2', id: 'VIDEO_ID_2', thumbnail: 'THUMBNAIL_URL_2', duration: 'DURATION_2' },
    { title: 'Example Video 3', channel: 'Example Channel 3', id: 'VIDEO_ID_3', thumbnail: 'THUMBNAIL_URL_3', duration: 'DURATION_3' },
  ];
  let currentVideo = 0;
  let player;

  // YouTube API search function
  async function searchVideos() {
    if (!apiKey) {
      alert('Please enter your YouTube API key');
      return;
    }
    const response = await fetch(`https://www.googleapis.com/youtube/v3/search?part=snippet&q=${searchQuery}&maxResults=10&type=video&key=${apiKey}`);
    const data = await response.json();
    videos = data.items.map((item) => ({
      title: item.snippet.title,
      channel: item.snippet.channelTitle,
      id: item.id.videoId,
      thumbnail: item.snippet.thumbnails.default.url,
      duration: 'Unknown', // YouTube API doesn't provide duration in search results
    }));
  }

  // Save API key to Local Storage
  function saveApiKey() {
    localStorage.setItem('apiKey', apiKey);
  }

  // Add video to playlist
  function addVideo(video) {
    playlist = [...playlist, video];
    localStorage.setItem('playlist', JSON.stringify(playlist));
  }

  // Remove video from playlist
  function removeVideo(index) {
    playlist = playlist.filter((_, i) => i !== index);
    localStorage.setItem('playlist', JSON.stringify(playlist));
  }

  // Sort playlist
  function sortPlaylist(by) {
    playlist = playlist.sort((a, b) => a[by].localeCompare(b[by]));
    localStorage.setItem('playlist', JSON.stringify(playlist));
  }

  // YouTube IFrame Player API callback
  // YouTube IFrame Player API callback
  let playerState = null;

  // YouTube IFrame Player API callback
  window.onYouTubeIframeAPIReady = () => {
    player = new YT.Player('player', {
      videoId: playlist[currentVideo].id,
      playerVars: {
        autoplay: 1,
        controls: 0,
      },
      events: {
        onStateChange: (event) => {
          playerState = event.data;
          if (event.data === YT.PlayerState.ENDED) {
            currentVideo = (currentVideo + 1) % playlist.length;
            player.loadVideoById(playlist[currentVideo].id);
          }
        },
      },
    });
  };

  // Playback controls
  function playPause() {
  if (player && player.getPlayerState && window.YT) {
    if (player.getPlayerState() === window.YT.PlayerState.PLAYING) {
      player.pauseVideo();
    } else {
      player.playVideo();
    }
  }
}


  function skipNext() {
  if (player && player.loadVideoById) {
    currentVideo = (currentVideo + 1) % playlist.length;
    player.loadVideoById(playlist[currentVideo].id);
  }
}

function skipPrevious() {
  if (player && player.loadVideoById) {
    currentVideo = (currentVideo - 1 + playlist.length) % playlist.length;
    player.loadVideoById(playlist[currentVideo].id);
  }
}

