            if (notes.length === 0) {
                createExampleNotes();
            }
        }

        // Create example notes on first load
        function createExampleNotes() {
            const exampleNotes = [
                {
                    id: generateId(),
                    title: 'Welcome to Note Organizer',
                    text: 'This is your first note! You can create, edit, and organize notes with tags. Use the search bar to find notes quickly and filter by tags.',
                    tags: ['welcome', 'tutorial'],
                    dateCreated: new Date().toISOString(),
                    dateModified: new Date().toISOString()
                },
                {
                    id: generateId(),
                    title: 'Project Ideas',
                    text: 'List of project ideas:\n- Build a personal website\n- Learn a new programming language\n- Create a mobile app\n- Write a blog about coding',
                    tags: ['work', 'ideas', 'projects'],
                    dateCreated: new Date(Date.now() - 86400000).toISOString(), // Yesterday
                    dateModified: new Date(Date.now() - 86400000).toISOString()
                },
                {
                    id: generateId(),
                    title: 'Shopping List',
                    text: 'Weekly groceries:\n- Milk\n- Bread\n- Eggs\n- Fruits\n- Vegetables\n- Coffee',
                    tags: ['personal', 'shopping'],
                    dateCreated: new Date(Date.now() - 172800000).toISOString(), // 2 days ago
                    dateModified: new Date(Date.now() - 172800000).toISOString()
                }
            ];

            notes = exampleNotes;
            saveNotes();
        }

        // Local storage functions
        function loadNotes() {
            const stored = localStorage.getItem('notes');
            if (stored) {
                notes = JSON.parse(stored);
            }
        }

        function saveNotes() {
            localStorage.setItem('notes', JSON.stringify(notes));
        }

        // Utility functions
        function generateId() {
            return Date.now().toString(36) + Math.random().toString(36).substr(2);
        }

        function formatDate(dateString) {
            const date = new Date(dateString);
            return date.toLocaleDateString('en-US', { 
                month: 'short', 
                day: 'numeric',
                hour: '2-digit',
                minute: '2-digit'
            });
        }

        function parseTags(tagsString) {
            return tagsString
                .split(',')
                .map(tag => tag.trim())
                .filter(tag => tag.length > 0);
        }

        // Computed properties
        $: allTags = [...new Set(notes.flatMap(note => note.tags))].sort();

        $: filteredNotes = notes
            .filter(note => {
                const matchesSearch = !searchQuery || 
                    note.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
                    note.text.toLowerCase().includes(searchQuery.toLowerCase()) ||
                    note.tags.some(tag => tag.toLowerCase().includes(searchQuery.toLowerCase()));

                const matchesTag = !selectedTag || note.tags.includes(selectedTag);

                return matchesSearch && matchesTag;
            })
            .sort((a, b) => {
                const dateA = new Date(a[sortBy]);
                const dateB = new Date(b[sortBy]);
                return dateB - dateA; // Most recent first
            });

        // Event handlers
        function handleSearch(event) {
            searchQuery = event.target.value;
        }

        function setSortBy(newSortBy) {
            sortBy = newSortBy;
        }

        function setSelectedTag(tag) {
            selectedTag = tag;
        }

        function openAddModal() {
            editingNote = null;
            formData = {
                title: '',
                text: '',
                tagsString: ''
            };
            showModal = true;
        }

        function editNote(note) {
            editingNote = note;
            formData = {
                title: note.title,
                text: note.text,
                tagsString: note.tags.join(', ')
            };
            showModal = true;
        }

        function closeModal() {
            showModal = false;
            editingNote = null;
        }

        function saveNote() {
            if (!formData.title.trim()) {
                alert('Please enter a title');
                return;
            }

            const tags = parseTags(formData.tagsString);
            const now = new Date().toISOString();

            if (editingNote) {
                // Update existing note
                const index = notes.findIndex(n => n.id === editingNote.id);
                notes[index] = {
                    ...editingNote,
                    title: formData.title.trim(),
                    text: formData.text.trim(),
                    tags: tags,
                    dateModified: now
                };
            } else {
                // Create new note
                const newNote = {
                    id: generateId(),
                    title: formData.title.trim(),
                    text: formData.text.trim(),
                    tags: tags,
                    dateCreated: now,
                    dateModified: now
                };
                notes = [newNote, ...notes];
            }

            saveNotes();
            closeModal();
        }

        function deleteNote() {
            if (confirm('Are you sure you want to delete this note?')) {
                notes = notes.filter(n => n.id !== editingNote.id);
                saveNotes();
                closeModal();
            }
        }

        // Initialize the app
        init();

