function ScoreCircle(node, { score, label }) {
  const strokeColor = score >= 8 ? "#10b981" : score >= 6 ? "#f59e0b" : "#ef4444";
  const strokeDasharray = `${score * 25.1} 251`;

  node.innerHTML = `
