import { onMount } from 'svelte';


  // Application State
  let isPlaying = false;
  let currentTrack = null;
  let searchResults = [];
  let activeTab = 'url';

  // Form inputs
  let urlInput = '';
  let searchInput = '';

  // Player state
  let currentTime = 0;
  let totalTime = 0;
  let volume = 50;
  let progress = 0;

  // Loading states
  let isLoadingUrl = false;
  let isSearching = false;

  // Toast notification
  let toast = { show: false, message: '', type: 'success' };

  // Audio element reference
  let audioElement;

  // Utility Functions
  function isValidYouTubeUrl(url) {
    const patterns = [
      /^https?:\/\/(www\.)?youtube\.com\/watch\?v=[\w-]+/,
      /^https?:\/\/youtu\.be\/[\w-]+/,
      /^https?:\/\/(www\.)?youtube\.com\/embed\/[\w-]+/
    ];
    return patterns.some(pattern => pattern.test(url));
  }

  function extractVideoId(url) {
    const patterns = [
      /(?:youtube\.com\/watch\?v=|youtu\.be\/|youtube\.com\/embed\/)([^&\n?#]+)/
    ];

    for (const pattern of patterns) {
      const match = url.match(pattern);
      if (match) return match[1];
    }
    return null;
  }

  function formatTime(seconds) {
    const mins = Math.floor(seconds / 60);
    const secs = Math.floor(seconds % 60);
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  }

  function showToast(message, type = 'success') {
    toast = { show: true, message, type };
    setTimeout(() => {
      toast.show = false;
    }, 3000);
  }

  // Audio Loading Functions
  async function loadAudioFromUrl() {
    if (!urlInput.trim()) {
      showToast('Please enter a YouTube URL', 'error');
      return;
    }

    if (!isValidYouTubeUrl(urlInput)) {
      showToast('Please enter a valid YouTube URL', 'error');
      return;
    }

    isLoadingUrl = true;

    try {
      showToast('Loading audio...', 'warning');

      const videoId = extractVideoId(urlInput);
      const videoInfo = await getVideoInfo(videoId);
      const audioFormat = chooseAudioFormat(videoInfo.formats);

      if (!audioFormat) {
        throw new Error('No audio stream available');
      }

