  function onTimeUpdate() {
    currentTime = audioElement.currentTime;
    if (audioElement.duration) {
      progress = (currentTime / audioElement.duration) * 100;
    }
  }

  function onLoadedMetadata() {
    totalTime = audioElement.duration;
  }

  function onError() {
