    }
  };

  const toneOptions = ['Professional', 'Creative', 'Educational', 'Helpful', 'Descriptive', 'Technical', 'Engaging', 'Casual', 'Formal', 'Conversational'];

  $: if (selectedCategory && categories[selectedCategory]) {
