/* Color Palette - Updated */
:root {
  --primary-color: #1ed4bc; /* Mint green accent color */
  --primary-hover: #19b8a3;
  --background-color: #121212; /* Dark background */
  --card-background: #1e1e1e; /* Slightly lighter than background */
  --text-primary: #ffffff;
  --text-secondary: #b3b3b3;
  --border-radius: 12px;
  --shadow: 0 8px 30px rgba(0, 0, 0, 0.2);
}

/* General Layout Updates */
.player-container {
  min-height: 100vh;
  background: var(--background-color);
  padding: 2rem;
  display: flex;
  flex-direction: column;
  color: var(--text-primary);
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, 'Open Sans', 'Helvetica Neue', sans-serif;
}

/* Header Section */
.header-section {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 1rem 1.5rem;
  margin-bottom: 2rem;
}

.header-content {
  width: 100%;
  display: flex;
  justify-content: space-between;
  align-items: center;
}

.logo-title {
  display: flex;
  align-items: center;
  gap: 0.5rem;
}

.logo {
  font-size: 1.8rem;
  color: var(--primary-color);
}

.header-content h1 {
  font-size: 1.5rem;
  font-weight: 600;
  color: var(--text-primary);
  margin: 0;
}

/* Search Bar */
.search-input {
  position: relative;
  width: 300px;
}

.search-icon {
  position: absolute;
  left: 10px;
  top: 50%;
  transform: translateY(-50%);
  color: var(--text-secondary);
}

.search-input input {
  width: 100%;
  padding: 0.75rem 1rem 0.75rem 2.5rem;
  background-color: rgba(255, 255, 255, 0.1);
  border: none;
  border-radius: 20px;
  color: var(--text-primary);
  font-size: 0.9rem;
}

.search-input input:focus {
  outline: none;
  background-color: rgba(255, 255, 255, 0.15);
}

.search-input input::placeholder {
  color: var(--text-secondary);
}

/* API Key Button */
.add-api-key-btn {
  background-color: var(--primary-color);
  color: #121212;
  font-weight: 600;
  padding: 0.5rem 1rem;
  border: none;
  border-radius: 20px;
  cursor: pointer;
  transition: background-color 0.2s, transform 0.1s;
}

.add-api-key-btn:hover {
  background-color: var(--primary-hover);
}

.add-api-key-btn:active {
  transform: scale(0.98);
}

/* Player Layout */
.player-layout {
  display: flex;
  gap: 2rem;
  max-width: 1200px;
  margin: 0 auto;
}

/* Album Art and Player */
.player-section {
  flex: 1;
  display: flex;
  flex-direction: column;
  align-items: center;
}

.album-art {
  width: 300px;
  height: 300px;
  margin-bottom: 2rem;
  border-radius: 12px;
  overflow: hidden;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.3);
  transition: transform 0.3s ease;
}

.album-art:hover {
  transform: scale(1.02);
}

.album-art img {
  width: 100%;
  height: 100%;
  object-fit: cover;
}

/* Track Info */
.track-info {
  text-align: center;
  margin-bottom: 1.5rem;
  width: 100%;
  max-width: 400px;
}

.track-info .title {
  font-size: 1.5rem;
  font-weight: 600;
  margin-bottom: 0.5rem;
  color: var(--text-primary);
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}

.artist-album {
  font-size: 1rem;
  color: var(--text-secondary);
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}

/* Progress Bar */
.time-progress {
  width: 100%;
  max-width: 400px;
  margin: 1rem 0;
  position: relative;
}

.progress-container {
  height: 4px;
  background: rgba(255, 255, 255, 0.1);
  border-radius: 2px;
  margin: 0.5rem 0;
  position: relative;
  cursor: pointer;
  transition: height 0.2s;
}

.progress-container:hover {
  height: 6px;
}

.progress-container:hover .progress-bar {
  background: var(--primary-hover);
}

.progress-bar {
  height: 100%;
  background: var(--primary-color);
  border-radius: 2px;
  transition: width 0.1s linear;
}

.time-display {
  display: flex;
  justify-content: space-between;
  font-size: 0.8rem;
  color: var(--text-secondary);
  margin-top: 0.25rem;
}

/* Controls */
.controls {
  display: flex;
  justify-content: center;
  align-items: center;
  gap: 2rem;
  margin: 1.5rem 0;
}

.control-btn {
  color: var(--text-primary);
  background: none;
  border: none;
  cursor: pointer;
  transition: transform 0.2s, color 0.2s;
  display: flex;
  align-items: center;
  justify-content: center;
}

.control-btn:hover {
  transform: scale(1.1);
  color: var(--primary-color);
}

.control-btn:active {
  transform: scale(0.95);
}

.play-btn {
  background-color: var(--primary-color);
  color: #121212;
  width: 60px;
  height: 60px;
  border-radius: 50%;
  display: flex;
  justify-content: center;
  align-items: center;
  transition: background-color 0.2s, transform 0.2s;
}

.play-btn:hover {
  background-color: var(--primary-hover);
  transform: scale(1.05);
}

.play-btn:active {
  transform: scale(0.98);
}

/* Volume Control */
.volume-control {
  display: flex;
  align-items: center;
  gap: 0.5rem;
  margin-top: 1rem;
}

.volume-button {
  background: none;
  border: none;
  color: var(--text-secondary);
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
}

.volume-slider {
  -webkit-appearance: none;
  width: 100px;
  height: 4px;
  border-radius: 2px;
  background: rgba(255, 255, 255, 0.1);
  outline: none;
  transition: opacity 0.2s;
}

.volume-slider::-webkit-slider-thumb {
  -webkit-appearance: none;
  appearance: none;
  width: 12px;
  height: 12px;
  border-radius: 50%;
  background: var(--primary-color);
  cursor: pointer;
  transition: transform 0.1s;
}

.volume-slider::-webkit-slider-thumb:hover {
  transform: scale(1.2);
}

.volume-slider::-moz-range-thumb {
  width: 12px;
  height: 12px;
  border-radius: 50%;
  background: var(--primary-color);
  cursor: pointer;
  border: none;
  transition: transform 0.1s;
}

.volume-slider::-moz-range-thumb:hover {
  transform: scale(1.2);
}

/* Playlist Section */
.playlist-section {
  width: 300px;
  background: rgba(255, 255, 255, 0.05);
  border-radius: var(--border-radius);
  padding: 1.5rem;
  height: fit-content;
}

.playlist-section h2 {
  font-size: 1.2rem;
  margin-bottom: 1rem;
  color: var(--text-primary);
  display: flex;
  justify-content: space-between;
  align-items: center;
}

.playlist-card {
  max-height: 400px;
  overflow-y: auto;
  margin-bottom: 1rem;
}

.playlist-card::-webkit-scrollbar {
  width: 6px;
}

.playlist-card::-webkit-scrollbar-track {
  background: rgba(255, 255, 255, 0.05);
  border-radius: 3px;
}

.playlist-card::-webkit-scrollbar-thumb {
  background: rgba(255, 255, 255, 0.2);
  border-radius: 3px;
}

.playlist-card::-webkit-scrollbar-thumb:hover {
  background: rgba(255, 255, 255, 0.3);
}

.playlist-list {
  list-style: none;
  padding: 0;
  margin: 0;
}

.playlist-item {
  padding: 0.75rem;
  border-radius: 8px;
  margin-bottom: 0.5rem;
  transition: background-color 0.2s;
  display: flex;
  justify-content: space-between;
  align-items: center;
}

.playlist-item:hover {
  background-color: rgba(255, 255, 255, 0.1);
}

.playlist-item.active {
  background-color: rgba(30, 212, 188, 0.2);
}

.playlist-item-info {
  flex: 1;
  overflow: hidden;
  display: flex;
  flex-direction: column;
}

.playlist-item-title {
  color: var(--text-primary);
  font-weight: 500;
  background: none;
  border: none;
  cursor: pointer;
  padding: 0;
  text-align: left;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
  max-width: 100%;
  font-size: 0.9rem;
}

.track-artist {
  color: var(--text-secondary);
  font-size: 0.8rem;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}

.remove-from-playlist {
  background: none;
  border: none;
  cursor: pointer;
  color: var(--text-secondary);
  opacity: 0.6;
  transition: opacity 0.2s, color 0.2s;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 0.25rem;
}

.remove-from-playlist:hover {
  opacity: 1;
  color: #ff5555;
}

/* Add to playlist button */
.add-to-playlist {
  background-color: transparent;
  border: 1px solid var(--primary-color);
  color: var(--primary-color);
  border-radius: 50%;
  width: 36px;
  height: 36px;
  display: flex;
  justify-content: center;
  align-items: center;
  cursor: pointer;
  margin: 0 auto;
  transition: background-color 0.2s, transform 0.1s;
}

.add-to-playlist:hover {
  background-color: rgba(30, 212, 188, 0.1);
  transform: scale(1.05);
}

.add-to-playlist:active {
  transform: scale(0.95);
}

/* Bottom Action Buttons */
.action-buttons {
  display: flex;
  justify-content: center;
  gap: 1rem;
  margin-top: 2rem;
  width: 100%;
  max-width: 400px;
}

.action-btn {
  padding: 0.75rem 1.5rem;
  border-radius: 20px;
  font-weight: 500;
  cursor: pointer;
  transition: background-color 0.2s, transform 0.1s;
  flex: 1;
  text-align: center;
}

.search-lyrics-btn {
  background-color: rgba(255, 255, 255, 0.1);
  color: var(--text-primary);
  border: none;
}

.search-lyrics-btn:hover {
  background-color: rgba(255, 255, 255, 0.15);
}

.save-playlist-btn {
  background-color: var(--primary-color);
  color: #121212;
  border: none;
}

.save-playlist-btn:hover {
  background-color: var(--primary-hover);
}

.action-btn:active {
  transform: scale(0.98);
}

/* Volume indicator */
.volume-indicator {
  position: absolute;
  right: 0;
  bottom: -20px;
  color: var(--text-secondary);
  font-size: 0.8rem;
}

/* Modal Styling */
.modal {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0, 0, 0, 0.8);
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 1rem;
  z-index: 1000;
  backdrop-filter: blur(5px);
}

.modal-content {
  background: var(--card-background);
  border-radius: var(--border-radius);
  padding: 2rem;
  width: 90%;
  max-width: 500px;
  max-height: 90vh;
  overflow-y: auto;
  position: relative;
  box-shadow: 0 15px 40px rgba(0, 0, 0, 0.3);
}

.modal-close {
  position: absolute;
  top: 1rem;
  right: 1rem;
  background: none;
  border: none;
  cursor: pointer;
  color: var(--text-secondary);
  display: flex;
  align-items: center;
  justify-content: center;
  transition: color 0.2s;
}

.modal-close:hover {
  color: var(--text-primary);
}

.modal h2 {
  font-size: 1.5rem;
  margin-bottom: 1.5rem;
  color: var(--text-primary);
}

.search-input,
.api-key-input {
  display: flex;
  gap: 0.5rem;
  margin-bottom: 1.5rem;
}

.search-input input,
.api-key-input input {
  flex: 1;
  padding: 0.75rem 1rem;
  background-color: rgba(255, 255, 255, 0.1);
  border: none;
  border-radius: 8px;
  color: var(--text-primary);
  font-size: 0.9rem;
}

.search-input input:focus,
.api-key-input input:focus {
  outline: none;
  background-color: rgba(255, 255, 255, 0.15);
}

.search-input button,
.api-key-input button {
  padding: 0.75rem 1.5rem;
  background: var(--primary-color);
  color: #121212;
  border: none;
  border-radius: 8px;
  cursor: pointer;
  font-size: 0.9rem;
  font-weight: 500;
  transition: background-color 0.2s;
}

.search-input button:hover,
.api-key-input button:hover {
  background: var(--primary-hover);
}

/* Search Results */
.search-results {
  display: flex;
  flex-direction: column;
  gap: 0.75rem;
  margin-top: 1rem;
}

.search-result-item {
  background: rgba(255, 255, 255, 0.05);
  border-radius: 8px;
  padding: 0.75rem 1rem;
  display: flex;
  justify-content: space-between;
  align-items: center;
  transition: background-color 0.2s;
}

.search-result-item:hover {
  background: rgba(255, 255, 255, 0.1);
}

.search-result-info {
  flex: 1;
  overflow: hidden;
}

.search-result-item span {
  display: block;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
  color: var(--text-primary);
  font-weight: 500;
  font-size: 0.9rem;
}

.search-result-item .artist {
  font-size: 0.8rem;
  color: var(--text-secondary);
  margin-top: 0.25rem;
}

.search-result-actions {
  display: flex;
  gap: 0.5rem;
}

.load-more {
  background: none;
  border: 1px solid var(--primary-color);
  color: var(--primary-color);
  padding: 0.75rem;
  border-radius: 8px;
  cursor: pointer;
  margin-top: 1rem;
  width: 100%;
  transition: background-color 0.2s;
  font-weight: 500;
}

.load-more:hover {
  background-color: rgba(30, 212, 188, 0.1);
}

/* Lyrics Section */
.lyrics-section {
  margin-top: 1rem;
  width: 100%;
  max-width: 400px;
}

.lyrics-toggle {
  width: 100%;
  padding: 0.75rem;
  background-color: rgba(255, 255, 255, 0.1);
  color: var(--text-primary);
  border: none;
  border-radius: var(--border-radius);
  font-size: 1rem;
  font-weight: 500;
  cursor: pointer;
  transition: background-color 0.2s;
}

.lyrics-toggle:hover {
  background-color: rgba(255, 255, 255, 0.15);
}

.lyrics-content {
  background-color: rgba(255, 255, 255, 0.05);
  border-radius: var(--border-radius);
  padding: 1.5rem;
  margin-top: 1rem;
  max-height: 300px;
  overflow-y: auto;
}

.lyrics-content::-webkit-scrollbar {
  width: 6px;
}

.lyrics-content::-webkit-scrollbar-track {
  background: rgba(255, 255, 255, 0.05);
  border-radius: 3px;
}

.lyrics-content::-webkit-scrollbar-thumb {
  background: rgba(255, 255, 255, 0.2);
  border-radius: 3px;
}

.lyrics-content::-webkit-scrollbar-thumb:hover {
  background: rgba(255, 255, 255, 0.3);
}

.lyrics-search {
  display: flex;
  gap: 0.5rem;
  margin-bottom: 1rem;
}

.lyrics-search input {
  flex: 1;
  padding: 0.75rem 1rem;
  background-color: rgba(255, 255, 255, 0.1);
  border: none;
  border-radius: 8px;
  color: var(--text-primary);
  font-size: 0.9rem;
}

.lyrics-search input:focus {
  outline: none;
  background-color: rgba(255, 255, 255, 0.15);
}

.lyrics-search button {
  padding: 0.75rem 1.5rem;
  background: var(--primary-color);
  color: #121212;
  border: none;
  border-radius: 8px;
  cursor: pointer;
  font-size: 0.9rem;
  font-weight: 500;
  transition: background-color 0.2s;
}

.lyrics-search button:hover {
  background: var(--primary-hover);
}

.lyrics-results-list {
  margin-top: 1rem;
  display: flex;
  flex-direction: column;
  gap: 0.75rem;
}

.lyrics-results-item {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 0.75rem;
  background: rgba(255, 255, 255, 0.05);
  border-radius: 8px;
  gap: 1rem;
  transition: background-color 0.2s;
}

.lyrics-results-item:hover {
  background: rgba(255, 255, 255, 0.1);
}

.lyrics-results-info {
  display: flex;
  flex-direction: column;
  gap: 0.25rem;
  flex: 1;
  overflow: hidden;
}

.song-title {
  font-weight: 500;
  color: var(--text-primary);
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}

.artist-name {
  font-size: 0.8rem;
  color: var(--text-secondary);
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}

.get-lyrics-btn {
  padding: 0.5rem 1rem;
  background: var(--primary-color);
  color: #121212;
  border: none;
  border-radius: 8px;
  cursor: pointer;
  font-size: 0.8rem;
  font-weight: 500;
  white-space: nowrap;
  transition: background-color 0.2s;
}

.get-lyrics-btn:hover {
  background: var(--primary-hover);
}

pre {
  white-space: pre-wrap;
  word-wrap: break-word;
  color: var(--text-primary);
  font-family: inherit;
  line-height: 1.6;
  font-size: 0.9rem;
}

/* YouTube Player (hidden) */
#youtube-player {
  position: absolute;
  top: -9999px;
  left: -9999px;
}

/* Responsive Design */
@media (max-width: 900px) {
  .player-layout {
    flex-direction: column;
  }

  .playlist-section {
    width: 100%;
    margin-top: 2rem;
  }

  .album-art {
    width: 250px;
    height: 250px;
  }

  .controls {
    gap: 1.5rem;
  }
}

@media (max-width: 600px) {
  .header-content {
    flex-direction: column;
    gap: 1rem;
  }

  .search-input {
    width: 100%;
  }

  .album-art {
    width: 200px;
    height: 200px;
  }

  .controls {
    gap: 1rem;
  }

  .play-btn {
    width: 50px;
    height: 50px;
  }

  .action-buttons {
    flex-direction: column;
    gap: 0.75rem;
  }
}

