import { onMount } from 'svelte';
  import {
    Search,
    X,
    MoreVertical,
    SkipBack,
    SkipForward,
    Play,
    Pause,
    Key,
    Volume2,
    Settings,
    List,
    Plus,
    FileText,
    Trash2,
    VolumeX,
    Volume1,
  } from 'lucide-svelte';

  // State variables
  let player;
  let currentTime = 0;
  let duration = 0;
  let isPlaying = false;
  let searchQuery = '';
  let showSearchModal = false;
  let showApiKeyModal = false;
  let searchResults = [];
  let displayedResults = 5;
  let playlist = [];
  let currentVideoId = '';
  let isSearching = false;
  let youtubeApiKey = '';
  let showPlaylist = false;
  let volume = 50;
  let isPlayerReady = false;
  let currentTrackIndex = -1;
  let currentTrackInfo = null;
  let manualSearchQuery = { title: '', artist: '' };
  let lyrics = null;
  let errorMessage = null;
  let lyricsSearchQuery = '';
  let lyricsSearchResults = [];
  let isSearchingLyrics = false;
  let selectedLyrics = null;
  let lyricsError = null;
  let playerError = null;
  let isInitializing = true;
  let showVolumeSlider = false;
  let showSettings = false;
  let showVolume = true;
  let showLyricsModal = false;
  let showLyricsSearch = false;
  let isPlaylistVisible = false;
  let showLyrics = false;
  let thumbnailUrl = defaultImage;

  //Settings icon method
  function toggleSettings() {
    showSettings = !showSettings;
  }

  function toggleVolume() {
    showVolume = !showVolume;
  }

  // Initialize YouTube Player
  onMount(() => {
    tryInitialize();
  });

  async function tryInitialize() {
    let retries = 0;
    const maxRetries = 3;

    async function attemptInitialization() {
      try {
        await loadYouTubeAPI();
        isInitializing = false;
      } catch (error) {
        console.error('Error loading YouTube API or initializing player:', error);
        playerError =
          'Failed to initialize YouTube player. Please check your internet connection or try again later.';

        if (retries < maxRetries) {
          retries++;
          console.log(`Retrying initialization (attempt ${retries}/${maxRetries})...`);
          setTimeout(attemptInitialization, 2000);
        } else {
          console.error('Max retries reached. Unable to initialize YouTube player.');
          isInitializing = false;
          playerError = 'Unable to initialize YouTube player after multiple attempts.';
        }
      }
    }
    await attemptInitialization();
  }

  // Load YouTube API
  function loadYouTubeAPI() {
    return new Promise((resolve, reject) => {
      if (window.YT && window.YT.Player) {
        initializePlayer();
        resolve();
      }

      const tag = document.createElement('script');
      tag.src = 'https://www.youtube.com/iframe_api';
      tag.onload = () => {
        window.onYouTubeIframeAPIReady = () => {
          initializePlayer();
          resolve();
        };
      };
      tag.onerror = () => {
        reject(new Error('Failed to load YouTube API'));
      };
      const firstScriptTag = document.getElementsByTagName('script')[0];
      firstScriptTag.parentNode.insertBefore(tag, firstScriptTag);
    });
  }

  // Initialize Player
  function initializePlayer() {
    console.log('Initializing YouTube player');
    try {
      player = new YT.Player('youtube-player', {
        height: '0',
        width: '0',
        videoId: '',
        events: {
          onReady: (event) => {
            console.log('YouTube player is ready');
            isPlayerReady = true;
            setVolume(volume);
            isInitializing = false; // Clear isInitializing flag here!
          },
          onStateChange: onPlayerStateChange,
          onError: onPlayerError,
        },
      });
    } catch (error) {
      console.error('Error initializing YouTube player:', error);
    }
  }

  // Handle Player Ready
  function onPlayerReady(event) {
    console.log('YouTube player is ready');
    isPlayerReady = true;
    setVolume(volume);

    if ('mediaSession' in navigator) {
      navigator.mediaSession.setActionHandler('play', () => togglePlay());
      navigator.mediaSession.setActionHandler('pause', () => togglePlay());
      navigator.mediaSession.setActionHandler('nexttrack', () => playNextTrack());
      navigator.mediaSession.setActionHandler('previoustrack', () => playPreviousTrack());
    }
  }

  // Handle Player State Change
  function onPlayerStateChange(event) {
    if (event.data === YT.PlayerState.PLAYING) {
      isPlaying = true;
      if ('mediaSession' in navigator) {
        navigator.mediaSession.playbackState = 'playing';
      }
      updateTimeInfo();
    } else if (event.data === YT.PlayerState.PAUSED) {
      isPlaying = false;
      if ('mediaSession' in navigator) {
        navigator.mediaSession.playbackState = 'paused';
      }
    } else if (event.data === YT.PlayerState.ENDED) {
      playNextTrack();
    }
  }

  function onPlayerError(event) {
    console.log('YouTube player error:', event.data);
    isPlaying = false;
  }

  function updateTimeInfo() {
    if (player && player.getCurrentTime && player.getDuration) {
      currentTime = player.getCurrentTime();
      duration = player.getDuration();
      if (isPlaying) {
        setTimeout(updateTimeInfo, 1000);
      }
    }
  }

  async function searchYouTube() {
    if (!youtubeApiKey) {
      alert('Please enter a YouTube API key first');
      showApiKeyModal = true;
      return;
    }
    if (!searchQuery.trim()) {
      alert('Please enter a search query');
      return;
    }
    isSearching = true;
    try {
      const response = await fetch(
        `https://www.googleapis.com/youtube/v3/search?part=snippet&q=${encodeURIComponent(
          searchQuery
        )}&type=video&key=${youtubeApiKey}&maxResults=25`
      );
      if (!response.ok) throw new Error(`HTTP error! status: ${response.status}`);
      const data = await response.json();
      searchResults = data.items.map((item) => ({
        title: item.snippet.title,
        artist: item.snippet.channelTitle,
        videoId: item.id.videoId,
      }));
    } catch (error) {
      console.error('Error fetching YouTube data:', error);
      alert('An error occurred while searching. Please try again.');
      searchResults = [];
    } finally {
      isSearching = false;
    }
  }

  function loadMore() {
    displayedResults += 5;
  }

  function addToPlaylist(track) {
    playlist = [...playlist, track];
  }

  function removeFromPlaylist(index) {
    playlist = playlist.filter((_, i) => i !== index);

    if (index === currentTrackIndex) {
      if (playlist.length > 0) {
        playVideo(playlist[0].videoId);
      } else {
        stopPlayback();
      }
    } else if (index < currentTrackIndex) {
      currentTrackIndex--;
    }
  }

  function stopPlayback() {
    currentTrackIndex = -1;
    currentVideoId = '';
    isPlaying = false;
    if (player && player.stopVideo) player.stopVideo();
    currentTrackInfo = null;

    if ('mediaSession' in navigator) {
      navigator.mediaSession.metadata = null;
    }
  }

  async function playVideo(videoId) {
    if (isInitializing) {
      console.log('YouTube player is still initializing. Please wait.');
      return;
    }
    if (!isPlayerReady || !player || !player.loadVideoById) {
      console.error('YouTube player is not fully initialized. Please wait.');
      return;
    }

    try {
      await player.loadVideoById(videoId);
      currentVideoId = videoId;
      currentTrackIndex = playlist.findIndex((track) => track.videoId === videoId);

      currentTrackInfo =
        playlist.find((track) => track.videoId === videoId) ||
        searchResults.find((result) => result.videoId === videoId) ||
        null;

      if (currentTrackInfo) {
        console.log('Current track info updated:', currentTrackInfo);
        thumbnailUrl = `https://img.youtube.com/vi/${videoId}/hqdefault.jpg`;

        if ('mediaSession' in navigator) {
          navigator.mediaSession.metadata = new MediaMetadata({
            title: currentTrackInfo.title || 'Unknown',
            artist: currentTrackInfo.artist || 'Unknown',
            artwork: [
              {
                src: thumbnailUrl,
                sizes: '480x360',
                type: 'image/jpeg',
              },
            ],
          });
        }
      } else {
        console.warn('Could not find track info for videoId:', videoId);
      }

      isPlaying = true;
      updateTimeInfo(); // Start updating time info
    } catch (error) {
      console.error(`Error loading video: ${error.message}. Please try again.`);
      isPlaying = false;
    }
  }

  function togglePlay() {
    if (!player) return;
    if (isPlaying) {
      player.pauseVideo();
      isPlaying = false;
    } else if (currentVideoId) {
      player.playVideo();
      isPlaying = true;
    } else if (playlist.length > 0) {
      playVideo(playlist[0].videoId);
    }
  }

  function formatTime(seconds) {
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = Math.floor(seconds % 60);
    return `${minutes}:${remainingSeconds.toString().padStart(2, '0')}`;
  }

  let previousVolume = null;
  function setVolume(value) {
    volume = value;
    if (player && player.setVolume) {
      player.setVolume(volume);

      // Update media session if available
      if ('mediaSession' in navigator) {
        // Some browsers support volume control via media session
        // This is just a placeholder as the actual implementation varies
      }
    }
  }

  function playNextTrack() {
    if (playlist.length === 0) return;
    let nextIndex = (currentTrackIndex + 1) % playlist.length;
    playVideo(playlist[nextIndex].videoId);
  }

  function playPreviousTrack() {
    if (playlist.length === 0) return;
    let prevIndex = currentTrackIndex === 0 ? playlist.length - 1 : currentTrackIndex - 1;
    playVideo(playlist[prevIndex].videoId);
  }

  function setApiKey() {
    if (youtubeApiKey.trim()) {
      showApiKeyModal = false;
    } else {
      alert('Please enter a valid API key');
    }
  }

  function handleSearchClick() {
    showSearchModal = true;
  }

  function handleApiKeyClick() {
    showApiKeyModal = true;
  }

  async function searchLyrics() {
    if (!lyricsSearchQuery.trim()) {
      alert('Please enter a search query');
      return;
    }
    isSearchingLyrics = true;
    lyricsError = null;
    lyricsSearchResults = [];
    selectedLyrics = null;

    try {
      const response = await fetch(
        `https://api.lyrics.ovh/suggest/${encodeURIComponent(lyricsSearchQuery)}`
      );
      if (!response.ok) throw new Error('Failed to search lyrics');
      const data = await response.json();
      lyricsSearchResults = data.data
        .slice(0, 5)
        .map((item) => ({
          title: item.title,
          artist: item.artist.name,
          id: item.id,
        }));
    } catch (error) {
      lyricsError = 'Failed to search for lyrics. Please try again.';
    } finally {
      isSearchingLyrics = false;
    }
  }

  async function getLyrics(title, artist) {
    lyricsError = null;
    selectedLyrics = null;

    try {
      const response = await fetch(
        `https://api.lyrics.ovh/v1/${encodeURIComponent(artist)}/${encodeURIComponent(title)}`
      );
      if (!response.ok) throw new Error('Lyrics not found');
      const data = await response.json();
      selectedLyrics = data.lyrics || 'Lyrics not available';
      showLyricsModal = true;
    } catch (error) {
      lyricsError = 'Could not retrieve lyrics. Please try another song.';
    }
  }

  function handleLyricsSearch() {
    searchLyrics();
  }

  const closeModal = () => {
    showLyricsModal = false;
  }

  const defaultImage = 'https://picsum.photos/id/237/300/300';

  function handleLyricsClick() {
    showLyricsSearch = !showLyricsSearch;
  }

  function handleSearchYouTubeClick() {
    showSearchModal = true;
  }

  function handleApiKeyModalClick() {
    showApiKeyModal = true;
  }

  function togglePlaylist() {
    isPlaylistVisible = !isPlaylistVisible;
  }

  function toggleLyrics() {
    showLyrics = !showLyrics;
  }

  function handleProgressKeydown(event) {
  if (!player || !player.getDuration) return;

  // Handle keyboard navigation
  if (event.key === 'ArrowLeft') {
    const newTime = Math.max(0, currentTime - 5); // Back 5 seconds
    player.seekTo(newTime, true);
    currentTime = newTime;
    event.preventDefault();
  } else if (event.key === 'ArrowRight') {
    const newTime = Math.min(duration, currentTime + 5); // Forward 5 seconds
    player.seekTo(newTime, true);
    currentTime = newTime;
    event.preventDefault();
  } else if (event.key === 'Enter' || event.key === ' ') {
    // Toggle play/pause on Enter or Space
    togglePlay();
    event.preventDefault();
  }
}

  function handleProgressBarClick(event) {
  if (!player || !player.getDuration) return;

  const progressBar = event.currentTarget;
  const rect = progressBar.getBoundingClientRect();
  const clickPosition = (event.clientX - rect.left) / rect.width;
  const newTime = clickPosition * duration;

  player.seekTo(newTime, true);
  currentTime = newTime;
}

function toggleMute() {
  if (volume === 0) {
    // Restore to previous volume or default to 50
    setVolume(previousVolume || 50);
  } else {
    // Store current volume and mute
    previousVolume = volume;
    setVolume(0);
  }
}
