/* Color Palette - Updated */
:root {
  --primary-color: #1ed4bc; /* Mint green accent color */
  --primary-hover: #19b8a3;
  --background-color: #121212; /* Dark background */
  --card-background: #1e1e1e; /* Slightly lighter than background */
  --text-primary: #ffffff;
  --text-secondary: #b3b3b3;
  --border-radius: 12px;
  --shadow: 0 8px 30px rgba(0, 0, 0, 0.2);
}

/* General Layout Updates */
.player-container {
  min-height: 100vh;
  background: var(--background-color);
  padding: 2rem;
  display: flex;
  flex-direction: column;
  color: var(--text-primary);
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, 'Open Sans', 'Helvetica Neue', sans-serif;
}

/* Header Section */
.header-section {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 1rem 1.5rem;
  margin-bottom: 2rem;
}

.header-content {
  width: 100%;
  display: flex;
  justify-content: space-between;
  align-items: center;
}

.logo-title {
  display: flex;
  align-items: center;
  gap: 0.5rem;
}

.logo {
  font-size: 1.8rem;
  color: var(--primary-color);
}

.header-content h1 {
  font-size: 1.5rem;
  font-weight: 600;
  color: var(--text-primary);
  margin: 0;
}

/* Search Bar */
.search-input {
  position: relative;
  width: 300px;
}

.search-icon {
  position: absolute;
  left: 10px;
  top: 50%;
  transform: translateY(-50%);
  color: var(--text-secondary);
}

.search-input input {
  width: 100%;
  padding: 0.75rem 1rem 0.75rem 2.5rem;
  background-color: rgba(255, 255, 255, 0.1);
  border: none;
  border-radius: 20px;
  color: var(--text-primary);
  font-size: 0.9rem;
}

.search-input input:focus {
  outline: none;
  background-color: rgba(255, 255, 255, 0.15);
}

.search-input input::placeholder {
  color: var(--text-secondary);
}

/* API Key Button */
.add-api-key-btn {
  background-color: var(--primary-color);
  color: #121212;
  font-weight: 600;
  padding: 0.5rem 1rem;
  border: none;
  border-radius: 20px;
  cursor: pointer;
  transition: background-color 0.2s, transform 0.1s;
}

.add-api-key-btn:hover {
  background-color: var(--primary-hover);
}

.add-api-key-btn:active {
  transform: scale(0.98);
}

/* Player Layout */
.player-layout {
  display: flex;
  gap: 2rem;
  max-width: 1200px;
  margin: 0 auto;
}

/* Album Art and Player */
.player-section {
  flex: 1;
  display: flex;
  flex-direction: column;
  align-items: center;
}

.album-art {
  width: 300px;
  height: 300px;
  margin-bottom: 2rem;
  border-radius: 12px;
  overflow: hidden;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.3);
  transition: transform 0.3s ease;
}

.album-art:hover {
  transform: scale(1.02);
}

.album-art img {
  width: 100%;
  height: 100%;
  object-fit: cover;
}

/* Track Info */
.track-info {
  text-align: center;
  margin-bottom: 1.5rem;
  width: 100%;
  max-width: 400px;
}

.track-info .title {
  font-size: 1.5rem;
  font-weight: 600;
  margin-bottom: 0.5rem;
  color: var(--text-primary);
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}

.artist-album {
  font-size: 1rem;
  color: var(--text-secondary);
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}

/* Progress Bar */
.time-progress {
  width: 100%;
  max-width: 400px;
  margin: 1rem 0;
  position: relative;
}

.progress-container {
  height: 4px;
  background: rgba(255, 255, 255, 0.1);
  border-radius: 2px;
  margin: 0.5rem 0;
  position: relative;
  cursor: pointer;
  transition: height 0.2s;
}

.progress-container:hover {
  height: 6px;
}

.progress-container:hover .progress-bar {
  background: var(--primary-hover);
}

.progress-bar {
  height: 100%;
  background: var(--primary-color);
  border-radius: 2px;
  transition: width 0.1s linear;
}

.time-display {
  display: flex;
  justify-content: space-between;
  font-size: 0.8rem;
  color: var(--text-secondary);
  margin-top: 0.25rem;
}

/* Controls */
.controls {
  display: flex;
  justify-content: center;
  align-items: center;
  gap: 2rem;
  margin: 1.5rem 0;
}

.control-btn {
  color: var(--text-primary);
  background: none;
  border: none;
  cursor: pointer;
  transition: transform 0.2s, color 0.2s;
  display: flex;
  align-items: center;
  justify-content: center;
}

.control-btn:hover {
  transform: scale(1.1);
  color: var(--primary-color);
}

.control-btn:active {
  transform: scale(0.95);
}

.play-btn {
  background-color: var(--primary-color);
  color: #121212;
  width: 60px;
  height: 60px;
  border-radius: 50%;
  display: flex;
  justify-content: center;
  align-items: center;
  transition: background-color 0.2s, transform 0.2s;
}

.play-btn:hover {
  background-color: var(--primary-hover);
  transform: scale(1.05);
}

.play-btn:active {
  transform: scale(0.98);
}

/* Volume Control */
.volume-control {
  display: flex;
  align-items: center;
  gap: 0.5rem;
  margin-top: 1rem;
}

.volume-button {
  background: none;
  border: none;
  color: var(--text-secondary);
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
}

.volume-slider {
  -webkit-appearance: none;
  width: 100px;
  height: 4px;
  border-radius: 2px;
  background: rgba(255, 255, 255, 0.1);
  outline: none;
  transition: opacity 0.2s;
}

.volume-slider::-webkit-slider-thumb {
  -webkit-appearance: none;
  appearance: none;
  width: 12px;
  height: 12px;
  border-radius: 50%;
  background: var(--primary-color);
  cursor: pointer;
  transition: transform 0.1s;
}

.volume-slider::-webkit-slider-thumb:hover {
  transform: scale(1.2);
}

.volume-slider::-moz-range-thumb {
  width: 12px;
  height: 12px;
  border-radius: 50%;
  background: var(--primary-color);
  cursor: pointer;
  border: none;
  transition: transform 0.1s;
}

.volume-slider::-moz-range-thumb:hover {
  transform: scale(1.2);
}

/* Playlist Section */
.playlist-section {
  width: 300px;
  background: rgba(255, 255, 255, 0.05);
  border-radius: var(--border-radius);
  padding: 1.5rem;
  height: fit-content;
}

.playlist-section h2 {
  font-size: 1.2rem;
  margin-bottom: 1rem;
  color: var(--text-primary);
  display: flex;
  justify-content: space-between;
  align-items: center;
}

.playlist-card {
  max-height: 400px;
  overflow-y: auto;
  margin-bottom: 1rem;
}

.playlist-card::-webkit-scrollbar {
  width: 6px;
}

.playlist-card::-webkit-scrollbar-track {
  background: rgba(255, 255, 255, 0.05);
  border-radius: 3px;
}

.playlist-card::-webkit-scrollbar-thumb {
  background: rgba(255, 255, 255, 0.2);
  border-radius: 3px;
}

.playlist-card::-webkit-scrollbar-thumb:hover {
  background: rgba(255, 255, 255, 0.3);
}

.playlist-list {
  list-style: none;
  padding: 0;
  margin: 0;
}

.playlist-item {
  padding: 0.75rem;
  border-radius: 8px;
  margin-bottom: 0.5rem;
  transition: background-color 0.2s;
  display: flex;
  justify-content: space-between;
  align-items: center;
}

.playlist-item:hover {
  background-color: rgba(255, 255, 255, 0.1);
}

.playlist-item.active {
  background-color: rgba(30, 212, 188, 0.2);
}

.playlist-item-info {
  flex: 1;
  overflow: hidden;
  display: flex;
  flex-direction: column;
}

.playlist-item-title {
  color: var(--text-primary);
  font-weight: 500;
  background: none;
  border: none;
  cursor: pointer;
  padding: 0;
  text-align: left;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
