  line-height: 1.6;
  font-size: 0.9rem;
}

/* YouTube Player (hidden) */
#youtube-player {
  position: absolute;
  top: -9999px;
  left: -9999px;
}

/* Responsive Design */
@media (max-width: 900px) {
  .player-layout {
    flex-direction: column;
  }

  .playlist-section {
    width: 100%;
    margin-top: 2rem;
  }

  .album-art {
    width: 250px;
    height: 250px;
  }

  .controls {
    gap: 1.5rem;
  }
}

@media (max-width: 600px) {
  .header-content {
    flex-direction: column;
    gap: 1rem;
  }

  .search-input {
    width: 100%;
  }

  .album-art {
    width: 200px;
    height: 200px;
  }

  .controls {
    gap: 1rem;
  }

  .play-btn {
    width: 50px;
    height: 50px;
  }

  .action-buttons {
    flex-direction: column;
    gap: 0.75rem;
  }
}

